import React, { useState, useEffect } from 'react';
import * as api from '../api';
import styles from './ProfilePage.module.css';

const ProfilePage = () => {
  const [profile, setProfile] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({ name: '' });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const { data } = await api.getProfile();
        setProfile(data);
        setFormData({ name: data.name });
      } catch (err) {
        setError('Failed to load profile.');
      } finally {
        setLoading(false);
      }
    };
    fetchProfile();
  }, []);

  const handleEditToggle = () => {
    setIsEditing(!isEditing);
    // Reset form data to original profile name when cancelling
    if (isEditing) {
      setFormData({ name: profile.name });
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const { data } = await api.updateProfile(formData);
      setProfile(data);
      setIsEditing(false);
    } catch (err) {
      setError('Failed to update profile.');
    } finally {
      setLoading(false);
    }
  };

  if (loading && !profile) {
    return <div className={styles.container}><p>Loading profile...</p></div>;
  }

  if (error) {
    return <div className={styles.container}><p className={styles.error}>{error}</p></div>;
  }

  return (
    <div className={styles.container}>
      <div className={styles.profileBox}>
        <h1 className={styles.title}>User Profile</h1>
        {isEditing ? (
          <form onSubmit={handleSubmit}>
            <div className={styles.field}>
              <label>Name</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                className={styles.input}
              />
            </div>
            <div className={styles.field}>
              <label>Email</label>
              <p className={styles.readOnly}>{profile.email}</p>
            </div>
            <div className={styles.buttonGroup}>
              <button type="submit" className={styles.saveButton} disabled={loading}>
                {loading ? 'Saving...' : 'Save Changes'}
              </button>
              <button type="button" className={styles.cancelButton} onClick={handleEditToggle}>
                Cancel
              </button>
            </div>
          </form>
        ) : (
          <>
            <div className={styles.field}>
              <label>Name</label>
              <p>{profile.name}</p>
            </div>
            <div className={styles.field}>
              <label>Email</label>
              <p>{profile.email}</p>
            </div>
            <button className={styles.editButton} onClick={handleEditToggle}>
              Edit Profile
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default ProfilePage;
