import React, { useEffect, useState } from 'react';
import { useMenu } from '../contexts/MenuContext';
import styles from './MenuBuilderPage.module.css';

const MenuBuilderPage = () => {
    const { menu, setMenu, fetchMenu, updateMenu, loading, error } = useMenu();
    const [isSaving, setIsSaving] = useState(false);
    const [isAddingCategory, setIsAddingCategory] = useState(false);
    const [newCategoryName, setNewCategoryName] = useState('');

    useEffect(() => {
        if (fetchMenu) {
            fetchMenu();
        }
    }, [fetchMenu]);

    // --- Category Handlers ---
    const handleAddNewCategory = () => {
        if (newCategoryName.trim() === '') {
            alert('Please enter a category name.');
            return;
        }
        const newCategory = { name: newCategoryName, items: [] };
        setMenu(prevMenu => ({
            ...prevMenu,
            categories: [...prevMenu.categories, newCategory]
        }));
        setNewCategoryName('');
        setIsAddingCategory(false);
    };

    const handleRemoveCategory = (catIndex) => {
        if (window.confirm("Are you sure you want to delete this category and all its items?")) {
            setMenu(prevMenu => ({
                ...prevMenu,
                categories: prevMenu.categories.filter((_, index) => index !== catIndex)
            }));
        }
    };
    
    const handleCategoryChange = (e, catIndex) => {
        const updatedCategories = [...menu.categories];
        updatedCategories[catIndex][e.target.name] = e.target.value;
        setMenu(prevMenu => ({ ...prevMenu, categories: updatedCategories }));
    };

    // --- Item Handlers ---
    const handleAddItem = (catIndex) => {
        const newItem = { name: 'New Item', price: 0.00, description: '', type: 'veg' };
        const updatedCategories = [...menu.categories];
        updatedCategories[catIndex].items.push(newItem);
        setMenu(prevMenu => ({ ...prevMenu, categories: updatedCategories }));
    };

    const handleRemoveItem = (catIndex, itemIndex) => {
        const updatedCategories = [...menu.categories];
        updatedCategories[catIndex].items.splice(itemIndex, 1);
        setMenu(prevMenu => ({ ...prevMenu, categories: updatedCategories }));
    };

    const handleItemChange = (e, catIndex, itemIndex) => {
        const updatedCategories = [...menu.categories];
        updatedCategories[catIndex].items[itemIndex][e.target.name] = e.target.value;
        setMenu(prevMenu => ({ ...prevMenu, categories: updatedCategories }));
    };
    
    // --- General Handlers ---
    const handleRestaurantNameChange = (e) => {
        setMenu(prevMenu => ({...prevMenu, restaurantName: e.target.value}));
    };

    const handleSaveChanges = async () => {
        setIsSaving(true);
        try {
            await updateMenu(menu);
            alert('Menu saved successfully!');
        } catch (err) {
            alert('Error saving menu. Please try again.');
        } finally {
            setIsSaving(false);
        }
    };

    if (loading) return <div className={styles.container}><p>Loading Menu Builder...</p></div>;
    if (error) return <div className={styles.container}><p className={styles.error}>{error}</p></div>;
    if (!menu) return <div className={styles.container}><p>Could not load menu data. Please try refreshing.</p></div>;

    return (
        <div className={styles.container}>
            <div className={styles.header}>
                <h1>Menu Builder</h1>
                <button onClick={handleSaveChanges} className={styles.saveButton} disabled={isSaving}>
                    {isSaving ? 'Saving...' : 'Save All Changes'}
                </button>
            </div>
            
            <div className={styles.restaurantNameSection}>
                <label htmlFor="restaurantName">Restaurant Name</label>
                <input type="text" id="restaurantName" name="restaurantName" value={menu.restaurantName} onChange={handleRestaurantNameChange} className={styles.restaurantNameInput}/>
            </div>

            {menu.categories.map((category, catIndex) => (
                <div key={catIndex} className={styles.categoryCard}>
                    <div className={styles.categoryHeader}>
                        <input type="text" name="name" value={category.name} onChange={(e) => handleCategoryChange(e, catIndex)} className={styles.categoryInput}/>
                        <button onClick={() => handleRemoveCategory(catIndex)} className={styles.deleteButton}>Delete Category</button>
                    </div>

                    {category.items.map((item, itemIndex) => (
                        <div key={itemIndex} className={styles.itemRow}>
                            <span className={`${styles.typeIndicator} ${styles[item.type]}`}></span>
                            <input type="text" name="name" value={item.name} onChange={(e) => handleItemChange(e, catIndex, itemIndex)} placeholder="Item Name" className={styles.itemInput}/>
                            <input type="text" name="description" value={item.description} onChange={(e) => handleItemChange(e, catIndex, itemIndex)} placeholder="Description" className={styles.itemInput}/>
                            <select name="type" value={item.type || 'veg'} onChange={(e) => handleItemChange(e, catIndex, itemIndex)} className={styles.typeSelect}>
                                <option value="veg">Veg</option>
                                <option value="non-veg">Non-Veg</option>
                                <option value="egg">Egg</option>
                            </select>
                            <input type="number" name="price" value={item.price} onChange={(e) => handleItemChange(e, catIndex, itemIndex)} placeholder="Price" className={styles.priceInput}/>
                            <button onClick={() => handleRemoveItem(catIndex, itemIndex)} className={styles.deleteItemButton}>Ã—</button>
                        </div>
                    ))}
                    <button onClick={() => handleAddItem(catIndex)} className={styles.addItemButton}>+ Add Item</button>
                </div>
            ))}

            {isAddingCategory ? (
                <div className={styles.addCategoryForm}>
                    <input
                        type="text"
                        value={newCategoryName}
                        onChange={(e) => setNewCategoryName(e.target.value)}
                        placeholder="New category name"
                        className={styles.addCategoryInput}
                    />
                    <button onClick={handleAddNewCategory} className={styles.addCategorySave}>Save</button>
                    <button onClick={() => setIsAddingCategory(false)} className={styles.addCategoryCancel}>Cancel</button>
                </div>
            ) : (
                <button onClick={() => setIsAddingCategory(true)} className={styles.addCategoryButton}>+ Add New Category</button>
            )}
        </div>
    );
};

export default MenuBuilderPage;
