import React, { useState, useEffect } from 'react';
import { useMenu } from '../contexts/MenuContext';
import styles from './TemplatesPage.module.css';

const TemplatesPage = () => {
    const { menu, updateMenu, loading, fetchMenu } = useMenu();
    const [selectedTemplate, setSelectedTemplate] = useState('classic');
    const [isSaving, setIsSaving] = useState(false);

    useEffect(() => {
        // Fetch menu data if it's not already loaded
        if (!menu) {
            fetchMenu();
        } else {
            setSelectedTemplate(menu.template || 'classic');
        }
    }, [menu, fetchMenu]);

    const handleSelectTemplate = (templateName) => {
        setSelectedTemplate(templateName);
    };

    const handleSaveChanges = async () => {
        setIsSaving(true);
        try {
            await updateMenu({ ...menu, template: selectedTemplate });
            alert('Template saved successfully!');
        } catch (error) {
            alert('Failed to save template.');
        } finally {
            setIsSaving(false);
        }
    };

    const templates = [
        { name: 'classic', displayName: 'Classic Modern' },
        { name: 'dark-elegance', displayName: 'Dark Elegance' },
        { name: 'garden-fresh', displayName: 'Garden Fresh' },
        { name: 'minimalist', displayName: 'Minimalist' },
    ];

    if (loading) {
        return <div className={styles.container}><p>Loading templates...</p></div>;
    }

    return (
        <div className={styles.container}>
            <div className={styles.header}>
                <h1>Choose Your Menu Template</h1>
                <button onClick={handleSaveChanges} className={styles.saveButton} disabled={isSaving}>
                    {isSaving ? 'Saving...' : 'Save Template'}
                </button>
            </div>
            <p className={styles.subtitle}>Select a design that best fits your restaurant's style. This will change the look and feel of your public menu.</p>
            
            <div className={styles.grid}>
                {templates.map((template) => (
                    <div
                        key={template.name}
                        className={`${styles.card} ${selectedTemplate === template.name ? styles.selected : ''}`}
                        onClick={() => handleSelectTemplate(template.name)}
                    >
                        <div className={`${styles.preview} ${styles[template.name]}`}>
                            {/* This is a simplified visual preview of the template */}
                            <div className={styles.previewHeader}></div>
                            <div className={styles.previewLine}></div>
                            <div className={styles.previewLineShort}></div>
                            <div className={styles.previewLine}></div>
                        </div>
                        <h3 className={styles.cardTitle}>{template.displayName}</h3>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default TemplatesPage;
