import React, { useState } from 'react';
import { useMenu } from '../contexts/MenuContext';
import { Link } from 'react-router-dom';
import styles from './DashboardPage.module.css';

// Helper function to calculate time difference
const timeSince = (date) => {
    if (!date) return "N/A";

    const seconds = Math.floor((new Date() - new Date(date)) / 1000);
    let interval = seconds / 31536000;
    if (interval > 1) return Math.floor(interval) + " years ago";
    interval = seconds / 2592000;
    if (interval > 1) return Math.floor(interval) + " months ago";
    interval = seconds / 86400;
    if (interval > 1) return Math.floor(interval) + " days ago";
    interval = seconds / 3600;
    if (interval > 1) return Math.floor(interval) + " hours ago";
    interval = seconds / 60;
    if (interval > 1) return Math.floor(interval) + " minutes ago";
    return "Just now";
};

const DashboardPage = () => {
  const { menu } = useMenu();
  const [isPromptVisible, setIsPromptVisible] = useState(true); // State to control prompt visibility

  const isMenuOld = menu?.updatedAt && (new Date() - new Date(menu.updatedAt)) / (1000 * 60 * 60 * 24) > 7;

  // Function to hide the prompt
  const handleDismissPrompt = () => {
      setIsPromptVisible(false);
  };

  return (
    <div className={styles.container}>
      <h1 className={styles.title}>Welcome, {menu?.restaurantName || 'Restaurant Owner'}!</h1>
      <p className={styles.subtitle}>This is your command center. What would you like to do today?</p>
      
      {/* Conditional Update Prompt with dismiss logic */}
      {isMenuOld && isPromptVisible && (
        <div className={styles.updatePromptCard}>
            <button onClick={handleDismissPrompt} className={styles.dismissButton} title="Dismiss">×</button>
            <div className={styles.icon}>🔔</div>
            <div>
                <h2>Keep Your Menu Fresh!</h2>
                <p>Your menu hasn't been updated in over a week. Would you like to review it now?</p>
            </div>
            <Link to="/dashboard/menu" className={styles.updateButton}>Update Menu</Link>
        </div>
      )}

      {/* Quick Actions Grid */}
      <div className={styles.actionsGrid}>
        <Link to="/dashboard/menu" className={styles.actionCard}>
          <div className={styles.icon}>📝</div>
          <h2>Edit Your Menu</h2>
          <p>Add categories, update items, and set prices.</p>
        </Link>
        <Link to="/dashboard/templates" className={styles.actionCard}>
          <div className={styles.icon}>🎨</div>
          <h2>Choose a Template</h2>
          <p>Select a beautiful design for your digital menu.</p>
        </Link>
        <Link to="/dashboard/publish" className={styles.actionCard}>
          <div className={styles.icon}>🚀</div>
          <h2>Publish & Get QR Code</h2>
          <p>Make your menu live and download your QR code.</p>
        </Link>
      </div>

      <div className={styles.divider}></div>

      {/* Stats Section */}
      <h2 className={styles.sectionTitle}>Your Menu at a Glance</h2>
      <div className={styles.statsGrid}>
          <div className={styles.statCard}>
              <h3>Menu Status</h3>
              <p className={menu?.isPublished ? styles.published : styles.draft}>
                  {menu?.isPublished ? 'Published' : 'Draft'}
              </p>
          </div>
          <div className={styles.statCard}>
              <h3>Categories</h3>
              <p>{menu?.categories?.length || 0}</p>
          </div>
          <div className={styles.statCard}>
              <h3>Total Items</h3>
              <p>{menu?.categories?.reduce((acc, cat) => acc + cat.items.length, 0) || 0}</p>
          </div>
          <div className={styles.statCard}>
              <h3>Last Updated</h3>
              <p>{timeSince(menu?.updatedAt)}</p>
          </div>
      </div>
    </div>
  );
};

export default DashboardPage;
