function AppWrapper() {
  const location = useLocation();
  const hideNavbarOnRoutes = [
    '/login',
    '/',
    '/register',
    '/forgot-password',
    '/reset-password'
  ];

  // Use startsWith for reset-password/:token and exact match for others
  const shouldHideNavbar = hideNavbarOnRoutes.some(path =>
    path.includes(':')
      ? location.pathname.startsWith(path.split(':')[0])
      : location.pathname === path
  );

  const isAuthenticated = Boolean(localStorage.getItem('token'));

  return (
    <>
      {!shouldHideNavbar && <Navbar />}
      <main>
        <Routes>
          {/* Public / Auth Routes */}
          <Route path="/" element={<LoginPage />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/register" element={<RegisterPage />} />
          <Route path="/forgot-password" element={<ForgotPasswordPage />} />
          <Route path="/reset-password/:token" element={<ResetPasswordPage />} />
          <Route path="/auth/success" element={<GoogleAuthSuccessPage />} />

          {/* Protected Routes */}
          <Route
            path="/dashboard"
            element={isAuthenticated ? <DashboardPage /> : <Navigate to="/login" />}
          />
          <Route
            path="/dashboard/menu"
            element={isAuthenticated ? <DashboardLayout /> : <Navigate to="/login" />}
          />
          <Route
            path="/builder"
            element={isAuthenticated ? <MenuBuilderPage /> : <Navigate to="/login" />}
          />
          <Route
            path="/templates"
            element={isAuthenticated ? <TemplatesPage /> : <Navigate to="/login" />}
          />
          <Route
            path="/publish"
            element={isAuthenticated ? <PublishPage /> : <Navigate to="/login" />}
          />
          <Route
            path="/settings"
            element={isAuthenticated ? <SettingsPage /> : <Navigate to="/login" />}
          />
          <Route
            path="/profile"
            element={isAuthenticated ? <ProfilePage /> : <Navigate to="/login" />}
          />
        </Routes>
      </main>
    </>
  );
}
