import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { ThemeProvider } from './contexts/ThemeContext';

// Import Components
import Navbar from './components/Navbar';
import DashboardLayout from './components/DashboardLayout';
import Footer from './components/Footer'; // Import the new Footer component
import styles from './App.module.css'; // Import the app's global styles

// Import Pages
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import DashboardPage from './pages/DashboardPage';
import MenuBuilderPage from './pages/MenuBuilderPage';
import TemplatesPage from './pages/TemplatesPage';
import PublishPage from './pages/PublishPage';
import ProfilePage from './pages/ProfilePage';
import SettingsPage from './pages/SettingsPage';
import ForgotPasswordPage from './pages/ForgotPasswordPage';
import ResetPasswordPage from './pages/ResetPasswordPage';
import GoogleAuthSuccessPage from './pages/GoogleAuthSuccessPage';

function App() {
  return (
    <ThemeProvider>
      {/* The MenuProvider has been removed as it's no longer needed */}
      <Router>
        <div className={styles.appWrapper}>
          <Navbar />
          <main className={styles.mainContent}>
            <Routes>
            {/* Auth Routes */}
            <Route path="/" element={<LoginPage />} />
            <Route path="/login" element={<LoginPage />} />
            <Route path="/register" element={<RegisterPage />} />
            <Route path="/forgot-password" element={<ForgotPasswordPage />} />
            <Route path="/reset-password/:token" element={<ResetPasswordPage />} />
            <Route path="/auth/success" element={<GoogleAuthSuccessPage />} />

            {/* Dashboard Nested Routes */}
            <Route path="/dashboard" element={<DashboardLayout />}>
              <Route index element={<DashboardPage />} />
              <Route path="menu/:menuId" element={<MenuBuilderPage />} />
              <Route path="templates" element={<TemplatesPage />} />
              <Route path="publish" element={<PublishPage />} />
            </Route>

            {/* Standalone Profile Route */}
            <Route path="/profile" element={<ProfilePage />} />
            <Route path="/settings" element={<SettingsPage />} />

            </Routes>
          </main>
          <Footer />
        </div>
      </Router>
    </ThemeProvider>
  );
}

export default App;
