import React, { createContext, useState, useContext } from 'react';
import * as api from '../api'; // We'll add the menu functions to our api file

const MenuContext = createContext();

export const useMenu = () => useContext(MenuContext);

export const MenuProvider = ({ children }) => {
  const [menu, setMenu] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const fetchMenu = async () => {
    try {
      setLoading(true);
      const { data } = await api.getMenu();
      setMenu(data);
    } catch (err) {
      setError('Failed to load menu.');
    } finally {
      setLoading(false);
    }
  };

  const updateMenu = async (updatedData) => {
    try {
        setLoading(true);
        const { data } = await api.updateMenu(updatedData);
        setMenu(data);
        return data; // Return the saved data
    } catch (err) {
        setError('Failed to save menu.');
        throw err; // Re-throw error to be caught in the component
    } finally {
        setLoading(false);
    }
  }

  const value = {
    menu,
    setMenu,
    loading,
    error,
    fetchMenu,
    updateMenu,
  };

  return (
    <MenuContext.Provider value={value}>
      {children}
    </MenuContext.Provider>
  );
};
